import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ProjectDetails.css';

const ProjectDetails = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const [project, setProject] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Edit mode state
  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState('');
  const [editedDescription, setEditedDescription] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [projectRes, tasksRes] = await Promise.all([
          axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/projects/${projectId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
          }),
          axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${projectId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
          }),
        ]);
        setProject(projectRes.data);
        setTasks(tasksRes.data);
        setEditedTitle(projectRes.data.title);
        setEditedDescription(projectRes.data.description);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError('Failed to load project or tasks');
        setLoading(false);
      }
    };

    fetchData();
  }, [projectId]);

  const handleAddTask = () => {
    navigate(`/tasks/add?projectId=${projectId}`);
  };

  const handleDeleteProject = async () => {
    const confirmDelete = window.confirm('Are you sure you want to delete this project?');
    if (!confirmDelete) return;

    try {
      await axios.delete(`${process.env.REACT_APP_API_BASE_URL}/api/projects/${projectId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
      });
      navigate('/projects');
    } catch (err) {
      console.error(err);
      alert('Failed to delete project');
    }
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setEditedTitle(project.title);
    setEditedDescription(project.description);
  };

  const handleSaveEdit = async () => {
    try {
      const response = await axios.put(
        `${process.env.REACT_APP_API_BASE_URL}/api/projects/${projectId}`,
        { title: editedTitle, description: editedDescription },
        { headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` } }
      );
      setProject(response.data);
      setIsEditing(false);
    } catch (err) {
      console.error(err);
      alert('Failed to update project');
    }
  };

  if (loading) return <div className="loader">Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="project-details-container">
      <div className="details-card">
        {isEditing ? (
          <>
            <input
              type="text"
              value={editedTitle}
              onChange={(e) => setEditedTitle(e.target.value)}
              className="edit-input"
            />
            <textarea
              value={editedDescription}
              onChange={(e) => setEditedDescription(e.target.value)}
              className="edit-textarea"
            />
            <div className="project-actions">
              <button className="save-btn" onClick={handleSaveEdit}>Save</button>
              <button className="cancel-btn" onClick={handleCancelEdit}>Cancel</button>
            </div>
          </>
        ) : (
          <>
            <h2>{project.title}</h2>
            <p>{project.description || 'No description available.'}</p>
            <p><strong>Created:</strong> {new Date(project.createdAt).toLocaleString()}</p>
            <p><strong>Last Updated:</strong> {new Date(project.updatedAt).toLocaleString()}</p>

            <div className="project-actions">
              <button className="edit-btn" onClick={handleEditClick}>Edit Project</button>
              <button className="delete-btn" onClick={handleDeleteProject}>Delete Project</button>
            </div>
          </>
        )}
      </div>

      <div className="task-section">
        <h3>Tasks</h3>
        {tasks.length === 0 ? (
          <div className="no-tasks">
            <p>No tasks assigned to this project yet.</p>
            <button onClick={handleAddTask} className="add-task-btn">Create New Task</button>
          </div>
        ) : (
          <>
            <button onClick={handleAddTask} className="add-task-btn">Add New Task</button>
            <ul className="task-list">
              {tasks.map((task) => (
                <li key={task._id} className="task-item">
                  <strong>{task.title}</strong> - {task.status}
                </li>
              ))}
            </ul>
          </>
        )}
      </div>
    </div>
  );
};

export default ProjectDetails;
