import React, { useEffect, useState } from 'react';
import '../styles/Dashboard.css';

function Dashboard() {
  const [user, setUser] = useState({ username: 'Ashok K' });
  const [stats, setStats] = useState({
    totalTasks: 4,
    completed: 4,
    inProgress: 0,
    projects: 4,
  });

  useEffect(() => {
    // Replace this with an actual API call
    setUser({ username: 'Ashok K' });
    setStats({
      totalTasks: 4,
      completed: 4,
      inProgress: 0,
      projects: 4,
    });
  }, []);

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>
      <p className="welcome">Welcome back, {user.username}!</p>

      <div className="card-container">
        <div className="card card-blue">
          <div className="card-icon">📝</div>
          <p className="card-label">Total Tasks</p>
          <p className="card-value">{stats.totalTasks}</p>
          <p className="card-note green">↑ +5% from last week</p>
        </div>

        <div className="card card-green">
          <div className="card-icon">✅</div>
          <p className="card-label">Completed</p>
          <p className="card-value">{stats.completed}</p>
          <p className="card-note green">↑ 100% completion rate</p>
        </div>

        <div className="card card-purple">
          <div className="card-icon">⏳</div>
          <p className="card-label">In Progress</p>
          <p className="card-value">{stats.inProgress}</p>
          <p className="card-note gray">0 tasks to do</p>
        </div>

        <div className="card card-pink">
          <div className="card-icon">👥</div>
          <p className="card-label">Projects</p>
          <p className="card-value">{stats.projects}</p>
          <p className="card-note green">↑ 4 active</p>
        </div>
      </div>

      {/* ✅ Quick Actions Section */}
      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <button className="new-project-btn">
          <span className="icon">🔵</span> New Project
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
