import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/CreateProjectForm.css';

const CreateProjectForm = ({ onProjectCreated }) => {
  const [formData, setFormData] = useState({ title: '', description: '' });
  const [popupMessage, setPopupMessage] = useState({ type: '', text: '' });
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const { title, description } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setPopupMessage({ type: '', text: '' });
  };

  const showPopup = (type, text) => {
    setPopupMessage({ type, text });
    setTimeout(() => setPopupMessage({ type: '', text: '' }), 3000);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description) {
      showPopup('error', 'All fields are required');
      return;
    }

    try {
      setIsLoading(true);
      await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/projects`, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
      });

      showPopup('success', 'Project created successfully');
      setFormData({ title: '', description: '' });
      setIsLoading(false);

      onProjectCreated();
      setTimeout(() => navigate('/projects'), 3000); // Navigate after 3 seconds
    } catch (err) {
      setIsLoading(false);
      showPopup('error', err.response?.data?.message || 'Error creating project');
    }
  };

  return (
    <div className="create-project-form">
      <h2>Create a New Project</h2>

      {popupMessage.text && (
        <div className={`popup-message ${popupMessage.type}`}>
          {popupMessage.text}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={title}
            onChange={handleChange}
            placeholder="Enter project title"
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={description}
            onChange={handleChange}
            placeholder="Enter project description"
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Creating Project...' : 'Create Project'}
        </button>
      </form>
    </div>
  );
};

export default CreateProjectForm;
