// src/components/ProjectDetails.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/ProjectDetails.css';

const ProjectDetails = () => {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const [project, setProject] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState('');
  const [editedDescription, setEditedDescription] = useState('');

  const [editingTask, setEditingTask] = useState(null);
  const [editedTaskTitle, setEditedTaskTitle] = useState('');
  const [editedTaskDescription, setEditedTaskDescription] = useState('');
  const [editedTaskStatus, setEditedTaskStatus] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [projectRes, tasksRes] = await Promise.all([
          axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/projects/${projectId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
          }),
          axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${projectId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
          }),
        ]);
        setProject(projectRes.data);
        setTasks(tasksRes.data);
        setEditedTitle(projectRes.data.title);
        setEditedDescription(projectRes.data.description || '');
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError('Project not found or failed to load.');
        setLoading(false);
      }
    };
    fetchData();
  }, [projectId]);

  const calculateProgress = () => {
    if (tasks.length === 0) return 0;
    const completed = tasks.filter(t => t.status === 'Completed').length;
    return (completed / tasks.length) * 100;
  };

  const handleEditClick = () => setIsEditing(true);
  const handleCancelEdit = () => {
    setIsEditing(false);
    setEditedTitle(project.title);
    setEditedDescription(project.description);
  };
  const handleSaveEdit = async () => {
    try {
      const res = await axios.put(`${process.env.REACT_APP_API_BASE_URL}/api/projects/${projectId}`,
        { title: editedTitle, description: editedDescription },
        { headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` } }
      );
      setProject(res.data);
      setIsEditing(false);
    } catch (err) {
      console.error(err);
      alert('Failed to update project');
    }
  };

  const handleTaskEditClick = (task) => {
    setEditingTask(task);
    setEditedTaskTitle(task.title);
    setEditedTaskDescription(task.description);
    setEditedTaskStatus(task.status);
  };
  const handleTaskCancelEdit = () => setEditingTask(null);
  const handleTaskSaveEdit = async () => {
    try {
      const res = await axios.put(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${editingTask._id}`,
        { title: editedTaskTitle, description: editedTaskDescription, status: editedTaskStatus },
        { headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` } }
      );
      setTasks(tasks.map(t => t._id === editingTask._id ? res.data : t));
      setEditingTask(null);
    } catch (err) {
      console.error(err);
      alert('Failed to update task');
    }
  };

  const handleAddTask = () => navigate(`/projects/${projectId}/tasks/new`);
  const handleDeleteProject = async () => {
    if (!window.confirm('Are you sure you want to delete this project?')) return;
    try {
      await axios.delete(`${process.env.REACT_APP_API_BASE_URL}/api/projects/${projectId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
      });
      navigate('/projects');
    } catch (err) {
      console.error(err);
      alert('Failed to delete project');
    }
  };

  const taskCounts = {
    pending: tasks.filter(t => t.status === 'Pending').length,
    inProgress: tasks.filter(t => t.status === 'In Progress').length,
    completed: tasks.filter(t => t.status === 'Completed').length
  };

  if (loading) return <div className="loader">Loading...</div>;

  return (
    <div className="project-details-wrapper">
      <div className="project-card">
        {isEditing ? (
          <>
            <input value={editedTitle} onChange={(e) => setEditedTitle(e.target.value)} className="edit-input" />
            <textarea value={editedDescription} onChange={(e) => setEditedDescription(e.target.value)} className="edit-textarea" />
          </>
        ) : (
          <>
            <h2>{project.title}</h2>
            <p>{project.description || 'No description available.'}</p>
            <p><strong>Created:</strong> {new Date(project.createdAt).toLocaleString()}</p>
            <p><strong>Last Updated:</strong> {new Date(project.updatedAt).toLocaleString()}</p>
          </>
        )}
        <div className="btn-group">
          {isEditing ? (
            <>
              <button onClick={handleSaveEdit} className="save-btn">Save</button>
              <button onClick={handleCancelEdit} className="cancel-btn">Cancel</button>
            </>
          ) : (
            <>
              <button onClick={handleEditClick} className="edit-btn">Edit Project</button>
              <button onClick={handleDeleteProject} className="delete-btn">Delete Project</button>
            </>
          )}
        </div>
        <div className="progress-container">
          <p>Project Progress</p>
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${calculateProgress()}%` }}></div>
          </div>
          <p>{Math.round(calculateProgress())}% Complete</p>
        </div>
      </div>

      <div className="task-card">
        <h3>Tasks</h3>
        <p>Pending: {taskCounts.pending}</p>
        <p>In Progress: {taskCounts.inProgress}</p>
        <p>Completed: {taskCounts.completed}</p>
        <button onClick={handleAddTask} className="add-task-btn">Add New Task</button>
        {tasks.map(task => (
          <div key={task._id} className="task-item">
            <strong>{task.title}</strong> - {task.status}
            <button className="edit-task-btn" onClick={() => handleTaskEditClick(task)}>Edit</button>
          </div>
        ))}
      </div>

      {editingTask && (
        <div className="popup-overlay">
          <div className="popup-form">
            <h3>Edit Task</h3>
            <input value={editedTaskTitle} onChange={(e) => setEditedTaskTitle(e.target.value)} />
            <textarea value={editedTaskDescription} onChange={(e) => setEditedTaskDescription(e.target.value)} />
            <select value={editedTaskStatus} onChange={(e) => setEditedTaskStatus(e.target.value)}>
              <option value="Pending">Pending</option>
              <option value="In Progress">In Progress</option>
              <option value="Completed">Completed</option>
            </select>
            <div className="btn-group">
              <button onClick={handleTaskSaveEdit} className="save-btn">Save</button>
              <button onClick={handleTaskCancelEdit} className="cancel-btn">Cancel</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProjectDetails;
