import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TaskManager({ projectId }) {
  const [tasks, setTasks] = useState([]);
  const [form, setForm] = useState({ title: '', description: '' });

  const token = localStorage.getItem('token');

  // Axios instance with auth header
  const axiosInstance = axios.create({
    baseURL: '/api/tasks',
    headers: {
      Authorization: `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
  });

  useEffect(() => {
    if (projectId) fetchTasks();
  }, [projectId]);

  const fetchTasks = async () => {
    try {
      const res = await axiosInstance.get(`/${projectId}`);
      setTasks(res.data);
    } catch (err) {
      console.error('Fetch tasks error:', err.response?.data || err.message);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axiosInstance.post('/', { ...form, projectId });
      setForm({ title: '', description: '' });
      fetchTasks();
    } catch (err) {
      console.error('Create task error:', err.response?.data || err.message);
    }
  };

  const markCompleted = async (id) => {
    try {
      await axiosInstance.put(`/${id}`, {
        status: 'Completed',
        completedAt: new Date().toISOString(),
      });
      fetchTasks();
    } catch (err) {
      console.error('Update task error:', err.response?.data || err.message);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axiosInstance.delete(`/${id}`);
      fetchTasks();
    } catch (err) {
      console.error('Delete task error:', err.response?.data || err.message);
    }
  };

  return (
    <div>
      <h2>Tasks for Project ID: {projectId}</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="title"
          value={form.title}
          onChange={handleChange}
          placeholder="Task Title"
          required
        />
        <input
          name="description"
          value={form.description}
          onChange={handleChange}
          placeholder="Task Description"
        />
        <button type="submit">Add Task</button>
      </form>

      <ul>
        {tasks.map((task) => (
          <li key={task._id}>
            <strong>{task.title}</strong> - {task.status}
            <button onClick={() => markCompleted(task._id)} disabled={task.status === 'Completed'}>
              Mark Completed
            </button>
            <button onClick={() => deleteTask(task._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskManager;
