import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/Dashboard.css';

function Dashboard() {
  const [user, setUser] = useState({ username: '' });
  const [stats, setStats] = useState({
    totalTasks: 0,
    completed: 0,
    inProgress: 0,
    projects: 0,
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        if (!token) {
          return; // If no token, do not proceed with fetching
        }

        const response = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/user`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setUser(response.data.user); // Assuming response contains { user }
        setStats(response.data.stats); // Assuming response contains { stats }
      } catch (error) {
        console.error('Error fetching user data', error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="dashboard">
      <h2>Dashboard</h2>
      <p className="welcome">Welcome back, {user.username}!</p>

      <div className="card-container">
        <div className="card card-blue">
          <div className="card-icon">üìù</div>
          <p className="card-label">Total Tasks</p>
          <p className="card-value">{stats.totalTasks}</p>
          <p className="card-note green">‚Üë +5% from last week</p>
        </div>

        <div className="card card-green">
          <div className="card-icon">‚úÖ</div>
          <p className="card-label">Completed</p>
          <p className="card-value">{stats.completed}</p>
          <p className="card-note green">‚Üë 100% completion rate</p>
        </div>

        <div className="card card-purple">
          <div className="card-icon">‚è≥</div>
          <p className="card-label">In Progress</p>
          <p className="card-value">{stats.inProgress}</p>
          <p className="card-note gray">0 tasks to do</p>
        </div>

        <div className="card card-pink">
          <div className="card-icon">üë•</div>
          <p className="card-label">Projects</p>
          <p className="card-value">{stats.projects}</p>
          <p className="card-note green">‚Üë 4 active</p>
        </div>
      </div>

      {/* ‚úÖ Quick Actions Section */}
      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <button className="new-project-btn">
          <span className="icon">üîµ</span> New Project
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
