import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import '../styles/TaskForm.css';

const TaskForm = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const projectId = queryParams.get('projectId');
  const taskId = queryParams.get('taskId');

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('pending');

  useEffect(() => {
    if (taskId) {
      const fetchTask = async () => {
        try {
          const res = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${projectId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
          });
          const task = res.data.find(t => t._id === taskId);
          if (task) {
            setTitle(task.title);
            setDescription(task.description);
            setStatus(task.status);
          }
        } catch (err) {
          console.error('Failed to fetch task', err);
        }
      };
      fetchTask();
    }
  }, [taskId, projectId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const taskData = { title, description, status, projectId };

    try {
      if (taskId) {
        await axios.put(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${taskId}`, taskData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
        });
      } else {
        await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/tasks`, taskData, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
        });
      }
      navigate(`/projects/${projectId}`);
    } catch (err) {
      console.error('Error saving task:', err);
    }
  };

  return (
    <div className="task-form-container">
      <h2>{taskId ? 'Edit Task' : 'Add New Task'}</h2>
      <form onSubmit={handleSubmit} className="task-form">
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Task Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <select value={status} onChange={(e) => setStatus(e.target.value)}>
          <option value="pending">Pending</option>
          <option value="in-progress">In Progress</option>
          <option value="completed">Completed</option>
        </select>
        <button type="submit">{taskId ? 'Update Task' : 'Create Task'}</button>
      </form>
    </div>
  );
};

export default TaskForm;
