import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const TaskForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('Pending');
  const [completedAt, setCompletedAt] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const projectId = localStorage.getItem('currentProjectId'); // Get projectId from localStorage

    // Validate inputs
    if (!title.trim()) {
      setError('Task title is required');
      return;
    }

    if (!projectId) {
      setError('Project ID is missing from localStorage');
      return;
    }

    const newTask = {
      title,
      description,
      status,
      projectId,
      completedAt: completedAt ? completedAt : undefined,
    };

    try {
      const token = localStorage.getItem('authToken');
      if (!token) {
        setError('No authentication token found');
        return;
      }

      const response = await axios.post(
        'http://localhost:5000/api/tasks',
        newTask,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 201) {
        navigate(`/projects/${projectId}`);
      }
    } catch (err) {
      setError(err.response ? err.response.data.message : 'Server error');
      console.error('Error creating task:', err);
    }
  };

  return (
    <div className="task-form">
      <h2>Create Task</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="title">Task Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="status">Status</label>
          <select
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>

        <div>
          <label htmlFor="completedAt">Completion Date (Optional)</label>
          <input
            type="date"
            id="completedAt"
            value={completedAt}
            onChange={(e) => setCompletedAt(e.target.value)}
          />
        </div>

        <button type="submit">Create Task</button>
      </form>
    </div>
  );
};

export default TaskForm;
