import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/CreateProjectForm.css';

const CreateProjectForm = ({ onProjectCreated }) => {  // Receive the onProjectCreated prop
  const [formData, setFormData] = useState({ title: '', description: '' });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false); 
  const navigate = useNavigate();

  const { title, description } = formData;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
    setSuccessMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!title || !description) {
      setError('All fields are required');
      return;
    }

    try {
      setIsLoading(true);
      const res = await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/projects`, formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
      });

      setSuccessMessage('Project created successfully');
      setFormData({ title: '', description: '' });
      setIsLoading(false);

      // Notify parent component to refresh project list
      onProjectCreated();  // Call this function to refresh the list

      navigate('/projects');
    } catch (err) {
      setIsLoading(false);
      setError(err.response?.data?.message || 'Error creating project');
    }
  };

  return (
    <div className="create-project-form">
      <h2>Create a New Project</h2>

      {error && <p className="error-msg">{error}</p>}
      {successMessage && <p className="success-msg">{successMessage}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={title}
            onChange={handleChange}
            placeholder="Enter project title"
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={description}
            onChange={handleChange}
            placeholder="Enter project description"
          />
        </div>
        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Creating Project...' : 'Create Project'}
        </button>
      </form>
    </div>
  );
};

export default CreateProjectForm;
