import React, { useEffect, useState, useRef } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import '../styles/Header.css';

function Header() {
  const location = useLocation();
  const navigate = useNavigate();
  const [user, setUser] = useState(() => {
    const storedUser = localStorage.getItem('user');
    return storedUser ? JSON.parse(storedUser) : null;
  });
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const isActive = (path) => location.pathname === path;

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('user');
    setUser(null); // Clear user from state
    navigate('/login');
  };

  // Optional: Redirect to login if user is null and you're on a protected page
  useEffect(() => {
    if (!user && location.pathname !== '/login') {
      navigate('/login');
    }
  }, [user, location.pathname, navigate]);

  return (
    <header className="header">
      <div className="logo">âœ… <span>TaskFlow</span></div>
      <nav className="nav-links">
        <Link to="/dashboard" className={isActive('/dashboard') ? 'active' : ''}>Dashboard</Link>
        <Link to="/projects" className={isActive('/projects') ? 'active' : ''}>Projects</Link>
        <Link to="/tasks" className={isActive('/tasks') ? 'active' : ''}>Tasks</Link>
      </nav>
      {user && (
        <div className="profile-container" ref={dropdownRef}>
          <div className="profile-icon" onClick={() => setDropdownOpen(!dropdownOpen)}>
            {user.username?.charAt(0).toUpperCase()}
          </div>
          {dropdownOpen && (
            <div className="dropdown-menu">
              <p><strong>{user.username}</strong></p>
              <p>{user.email}</p>
              <button onClick={handleLogout}>Logout</button>
            </div>
          )}
        </div>
      )}
    </header>
  );
}

export default Header;
