import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ProjectCard from './ProjectCard';
import '../styles/ProjectList.css';
import { useNavigate } from 'react-router-dom';

const ProjectList = ({ refreshProjects }) => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProjectsWithTasks = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const res = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/projects`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        const projects = res.data;

        // Fetch tasks for each project and attach to project object
        const projectsWithTasks = await Promise.all(
          projects.map(async (project) => {
            try {
              const taskRes = await axios.get(
                `${process.env.REACT_APP_API_BASE_URL}/api/tasks/${project._id}`,
                {
                  headers: { Authorization: `Bearer ${token}` },
                }
              );
              return { ...project, tasks: taskRes.data };
            } catch (taskErr) {
              console.error(`Failed to fetch tasks for project ${project._id}`);
              return { ...project, tasks: [] };
            }
          })
        );

        setProjects(projectsWithTasks);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError('Failed to fetch projects');
        setLoading(false);
      }
    };

    fetchProjectsWithTasks();
  }, [refreshProjects]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="project-list-container">
      <div className="project-header">
        <h2>Your Projects</h2>
        <button
          className="create-project-btn"
          onClick={() => navigate('/projects/create')}
        >
          Create New Project
        </button>
      </div>

      <div className="project-list">
        {projects.length > 0 ? (
          projects.map((project) => (
            <ProjectCard key={project._id} project={project} />
          ))
        ) : (
          <div className="no-projects">
            <p>No projects found.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProjectList;
