// src/components/Task/TaskForm.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const TaskForm = () => {
  const { projectId } = useParams();
  const [task, setTask] = useState({ title: '', description: '', status: 'Pending' });
  const [isEditing, setIsEditing] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const taskId = new URLSearchParams(window.location.search).get('taskId');
    if (taskId) {
      setIsEditing(true);
      // Fetch existing task data
      axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${taskId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
      })
      .then((response) => {
        setTask(response.data);
      })
      .catch((error) => console.error('Error fetching task data:', error));
    }
  }, [projectId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        const taskId = new URLSearchParams(window.location.search).get('taskId');
        await axios.put(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${taskId}`, task, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
        });
      } else {
        await axios.post(`${process.env.REACT_APP_API_BASE_URL}/api/tasks`, { ...task, projectId }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
        });
      }
      navigate(`/projects/${projectId}`);
    } catch (error) {
      console.error('Error saving task:', error);
    }
  };

  return (
    <div className="task-form-container">
      <h2>{isEditing ? 'Edit Task' : 'Create New Task'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input
            type="text"
            value={task.title}
            onChange={(e) => setTask({ ...task, title: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            value={task.description || ''}
            onChange={(e) => setTask({ ...task, description: e.target.value })}
          />
        </div>
        <div>
          <label>Status:</label>
          <select
            value={task.status}
            onChange={(e) => setTask({ ...task, status: e.target.value })}
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>
        <button type="submit">{isEditing ? 'Save Changes' : 'Create Task'}</button>
      </form>
    </div>
  );
};

export default TaskForm;
