import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/TaskDetails.css';

const TaskDetails = () => {
  const { taskId, projectId } = useParams();
  const navigate = useNavigate();
  const [task, setTask] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const res = await axios.get(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${projectId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
        });
        const foundTask = res.data.find(t => t._id === taskId);
        setTask(foundTask);
      } catch (err) {
        console.error('Error fetching task:', err);
      }
    };
    fetchTasks();
  }, [taskId, projectId]);

  const handleDelete = async () => {
    try {
      await axios.delete(`${process.env.REACT_APP_API_BASE_URL}/api/tasks/${taskId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('authToken')}` },
      });
      navigate(`/projects/${projectId}`);
    } catch (err) {
      console.error('Error deleting task:', err);
    }
  };

  if (!task) return <p>Loading task details...</p>;

  return (
    <div className="task-details-container">
      <h2>{task.title}</h2>
      <p>{task.description}</p>
      <p><strong>Status:</strong> {task.status}</p>
      <button onClick={() => navigate(`/tasks/add?projectId=${projectId}&taskId=${task._id}`)}>Edit</button>
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default TaskDetails;
