import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TaskList.css';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [statusFilter, setStatusFilter] = useState('');
  const [projectFilter, setProjectFilter] = useState('');
  const [filteredTasks, setFilteredTasks] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const user = JSON.parse(localStorage.getItem('user'));
        const userId = user?.id;

        if (!token || !userId) {
          setError('Unauthorized. Please log in again.');
          setLoading(false);
          return;
        }

        const [taskResponse, projectResponse] = await Promise.all([
          axios.get('http://localhost:5000/api/tasks', {
            headers: { Authorization: `Bearer ${token}` },
            params: { createdBy: userId },
          }),
          axios.get('http://localhost:5000/api/projects', {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        setTasks(taskResponse.data);
        setProjects(projectResponse.data || []);
        setFilteredTasks(taskResponse.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch tasks or projects.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    let filtered = tasks;

    if (statusFilter) {
      filtered = filtered.filter(task => task.status === statusFilter);
    }

    if (projectFilter) {
      filtered = filtered.filter(task => task.projectId === projectFilter);
    }

    setFilteredTasks(filtered);
  }, [statusFilter, projectFilter, tasks]);

  const handleDelete = async (taskId) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;

    try {
      const token = localStorage.getItem('authToken');
      await axios.delete(`http://localhost:5000/api/tasks/${taskId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(tasks.filter(task => task._id !== taskId));
    } catch (err) {
      alert('Failed to delete task.');
    }
  };

  const handleEdit = (taskId) => {
    // Redirect to task edit page
    window.location.href = `/edit-task/${taskId}`;
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Your Tasks</h2>

      <div className="filters">
        <label>Status: </label>
        <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)}>
          <option value="">All</option>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
        </select>

        <label>Project: </label>
        <select value={projectFilter} onChange={(e) => setProjectFilter(e.target.value)}>
          <option value="">All</option>
          {projects.map((project) => (
            <option key={project._id} value={project._id}>
              {project.title}
            </option>
          ))}
        </select>
      </div>

      {filteredTasks.length === 0 ? (
        <p>No tasks found</p>
      ) : (
        <ul>
          {filteredTasks.map((task) => (
            <li key={task._id}>
              <h4>{task.title}</h4>
              <p>{task.description}</p>
              <p>Status: {task.status}</p>
              <p>
                Project: {projects.find((p) => p._id === task.projectId)?.title || 'Unknown'}
              </p>
              <p>Created At: {new Date(task.createdAt).toLocaleString()}</p>
              <p>Updated At: {new Date(task.updatedAt).toLocaleString()}</p>
              <button onClick={() => handleEdit(task._id)}>Edit</button>
              <button onClick={() => handleDelete(task._id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
