import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [statusFilter, setStatusFilter] = useState('');
  const [filteredTasks, setFilteredTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const token = localStorage.getItem('authToken');
        const user = JSON.parse(localStorage.getItem('user'));
        const userId = user?.id;

        if (!token || !userId) {
          setError('Unauthorized. Please log in again.');
          setLoading(false);
          return;
        }

        const response = await axios.get('http://localhost:5000/api/tasks', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
          params: {
            createdBy: userId,
          },
        });

        setTasks(response.data);
        setFilteredTasks(response.data); // Initially, show all tasks
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch tasks. Please try again.');
        setLoading(false);
      }
    };

    fetchTasks();
  }, []);

  useEffect(() => {
    if (statusFilter === '') {
      setFilteredTasks(tasks); // If no filter is applied, show all tasks
    } else {
      setFilteredTasks(tasks.filter(task => task.status === statusFilter)); // Filter tasks based on status
    }
  }, [statusFilter, tasks]);

  const handleStatusChange = (e) => {
    setStatusFilter(e.target.value); // Update filter based on user selection
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Your Tasks</h2>
      <div>
        <label>Filter by Status: </label>
        <select value={statusFilter} onChange={handleStatusChange}>
          <option value="">All</option>
          <option value="Pending">Pending</option>
          <option value="In Progress">In Progress</option>
          <option value="Completed">Completed</option>
        </select>
      </div>

      {filteredTasks.length === 0 ? (
        <p>No tasks found</p>
      ) : (
        <ul>
          {filteredTasks.map((task) => (
            <li key={task._id}>
              <h4>{task.title}</h4>
              <p>{task.description}</p>
              <p>Status: {task.status}</p>
              <p>Completed At: {task.completedAt ? new Date(task.completedAt).toLocaleDateString() : 'N/A'}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TaskList;
