import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

const TaskDetails = () => {
  const [task, setTask] = useState(null);
  const [error, setError] = useState(null);
  const [status, setStatus] = useState('');
  const [completedAt, setCompletedAt] = useState('');
  const { taskId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTask = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/tasks/${taskId}`);
        setTask(response.data);
        setStatus(response.data.status);
        setCompletedAt(response.data.completedAt ? response.data.completedAt.substring(0, 10) : '');
      } catch (err) {
        setError(err.response ? err.response.data.message : 'Error fetching task');
      }
    };

    fetchTask();
  }, [taskId]);

  const handleUpdate = async (e) => {
    e.preventDefault();

    const updatedTask = {
      status,
      completedAt: completedAt ? new Date(completedAt) : null,
    };

    try {
      const response = await axios.put(`http://localhost:5000/api/tasks/${taskId}`, updatedTask);
      if (response.status === 200) {
        navigate(`/projects/${task.projectId}`);
      }
    } catch (err) {
      setError(err.response ? err.response.data.message : 'Error updating task');
    }
  };

  if (error) return <p>{error}</p>;

  return (
    <div>
      {task ? (
        <div className="task-details">
          <h2>Task: {task.title}</h2>
          <p>{task.description}</p>
          <form onSubmit={handleUpdate}>
            <div>
              <label htmlFor="status">Status</label>
              <select
                id="status"
                value={status}
                onChange={(e) => setStatus(e.target.value)}
              >
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </div>

            <div>
              <label htmlFor="completedAt">Completion Date</label>
              <input
                type="date"
                id="completedAt"
                value={completedAt}
                onChange={(e) => setCompletedAt(e.target.value)}
              />
            </div>

            <button type="submit">Update Task</button>
          </form>
        </div>
      ) : (
        <p>Loading task details...</p>
      )}
    </div>
  );
};

export default TaskDetails;
