import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import SignupForm from './components/Auth/SignupForm';
import LoginForm from './components/Auth/LoginForm';
import ProjectList from './components/project/ProjectList';
import CreateProjectForm from './components/project/CreateProjectForm';
import ProjectDetails from './components/project/ProjectDetails';
import TaskForm from './components/tasks/TaskForm';
import TaskDetails from './components/tasks/TaskDetails';
import TaskList from './components/tasks/TaskList';
import Header from './components/Layout/Header'; // Your header component
import Dashboard from './components/Dashboard/Dashboard'; // Your dashboard component

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Hook to programmatically navigate

  useEffect(() => {
    // Check if user is logged in by checking for an auth token in localStorage
    const userLoggedIn = localStorage.getItem('authToken') !== null;
    setIsLoggedIn(userLoggedIn);
  }, []);

  const handleLogin = () => {
    // Set auth token in localStorage
    localStorage.setItem('authToken', 'your-auth-token');
    // Update login state
    setIsLoggedIn(true);
    // Navigate to the Dashboard page immediately after login
    navigate('/');
  };

  const handleLogout = () => {
    // Remove auth token from localStorage
    localStorage.removeItem('authToken');
    // Update login state
    setIsLoggedIn(false);
    // Optionally, navigate to login page
    navigate('/login');
  };

  return (
    <Router>
      <div className="app">
        {isLoggedIn && <Header onLogout={handleLogout} />} {/* Show header if logged in */}

        <Routes>
          {/* Authentication Routes */}
          <Route path="/" element={isLoggedIn ? <Dashboard /> : <Navigate to="/login" />} />
          <Route path="/signup" element={<SignupForm />} />
          <Route path="/login" element={<LoginForm onLogin={handleLogin} />} />

          {/* Projects Routes */}
          <Route path="/projects" element={<ProjectList />} />
          <Route path="/projects/create" element={<CreateProjectForm />} />

          {/* Nested Routes for Project Details and Tasks */}
          <Route path="/projects/:projectId" element={<ProjectDetails />}>
            <Route path="tasks" element={<TaskList />} /> {/* Nested Task List route under ProjectDetails */}
            <Route path="tasks/:taskId" element={<TaskDetails />} /> {/* Task Details route */}
            <Route path="tasks/new" element={<TaskForm />} /> {/* New Task Form */}
          </Route>

        </Routes>
      </div>
    </Router>
  );
};

export default App;
