import React, { useState } from 'react';
import axios from 'axios';

const TaskForm = ({ onClose, onTaskCreated }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('Pending');
  const [completedAt, setCompletedAt] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const projectId = localStorage.getItem('currentProjectId');

    if (!title.trim()) return setError('Task title is required');
    if (!projectId) return setError('Project ID missing');

    const newTask = {
      title,
      description,
      status,
      projectId,
      completedAt: completedAt || undefined,
    };

    try {
      const token = localStorage.getItem('authToken');
      if (!token) return setError('No auth token found');

      const response = await axios.post(
        'http://localhost:5000/api/tasks',
        newTask,
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.status === 201) {
        onTaskCreated && onTaskCreated(); // optional callback
        onClose(); // close modal
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Server error');
    }
  };

  return (
    <div className="popup-overlay">
      <div className="popup-form">
        <h2>Create Task</h2>
        {error && <p className="error">{error}</p>}
        <form onSubmit={handleSubmit}>
          <label>Task Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            className="edit-input"
          />

          <label>Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="edit-textarea"
          />

          <label>Status</label>
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            className="status-select"
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>

          <label>Completion Date (Optional)</label>
          <input
            type="date"
            value={completedAt}
            onChange={(e) => setCompletedAt(e.target.value)}
            className="edit-input"
          />

          <div className="modal-btn-group">
            <button type="submit" className="save-btn">Create</button>
            <button type="button" className="cancel-btn" onClick={onClose}>Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TaskForm;
